function display_counts(t) {
	animate_counts(t), jQuery(window).scroll(function() {
		animate_counts(t)
	})
}

function animate_counts(t) {
	var e = jQuery(window);
	e.scrollTop() > jQuery(".fact").offset().top - e.height() && (jQuery(".fact").animate({
		opacity: "1"
	}, 800), jQuery(".fact-number." + t.type).countTo({
		from: 0,
		to: t.stats,
		speed: t.speed,
		refreshInterval: t.interval,
		onComplete: function(e) {
			jQuery(this).removeClass(t.type);
			var n = parseInt(jQuery(this).text());
			jQuery(this).html(numeral(n).format("0,0"))
		}
	}))
}
